mapper.update:
UPDATE qna
SET qna_state = '답변'
   ,qna_reply = #{qnaReply}
   ,reply_date = sysdate
WHERE qna_no = #{qnaNo}

mapper.insert:

SELECT NVL(MAX(module_no), 0) + 1
FROM module

INSERT INTO module(
     module_no
    ,module_name
    ,module_price
    ,module_notice
    ,module_icon
    			   )
VALUES(
		 #{moduleNo}
		,#{moduleName}
		,#{modulePrice}
		,#{moduleNotice}
		,#{moduleIcon}
	   )


			SELECT NVL(MAX(mod_file_no), 0) + 1
			FROM mod_file

		INSERT INTO mod_file(
                     mod_file_no
                    ,mod_file_name
                    ,mod_file_content
                    ,mod_file_path
                    ,mod_file_turn
                    ,mod_file_type
                    ,mod_no
                    ,mod_file_originalname
                    )
		VALUES(
				 #{modFileNo}
				,#{modFileName}
				,#{modFileContent}
				,#{modFilePath}
				,#{modFileTurn}
				,#{modFileType}
				,#{modNo}
				,#{modFileOriginalname}
				)

mapper.update:
UPDATE module
SET module_name = #{moduleName}
   ,module_price = #{modulePrice}
   ,module_notice = #{moduleNotice}

   ,module_icon = #{moduleIcon}

WHERE module_no = #{moduleNo}
