mapper.update:
UPDATE qna
SET qna_state = '답변',
    qna_reply = #{qnaReply}
WHERE qna_no = #{qnaNo}

mapper.insert:

SELECT NVL(MAX(module_no), 0) + 1
FROM module

INSERT INTO module(
     module_no
    ,module_name
    ,module_price
    			   )
VALUES(
		 #{moduleNo}
		,#{moduleName}
		,#{modulePrice}
	   )


			SELECT NVL(MAX(mod_file_no), 0) + 1
			FROM mod_file

		INSERT INTO mod_file(
                     mod_file_no
                    ,mod_file_name
                    ,mod_file_content
                    ,mod_file_path
                    ,mod_file_turn
                    ,mod_file_type
                    ,qna_no
                    )
		VALUES(
				 #{modFileNo}
				,#{modFileName}
				,#{modFileContent}
				,#{modFilePath}
				,#{modFileTurn}
				,#{modFileType}
				,#{qnaNo}
				)
