mapper.insert:

		SELECT NVL(MAX(board_no),0) + 1
		FROM board

INSERT INTO board
	(
		board_no
		, board_title
		, board_content
		, employee_no
		, write_date
		, viewCnt
		, board_category
		, company_code
		)	
	VALUES
	(
		#{boardNo}
		, #{boardTitle}
		, #{boardContent}
		, #{employeeNo}
		, sysDate
		, #{viewCnt}
		, #{boardCategory}
		, #{companyCode}
	)

mapper.update:
UPDATE board
   SET
   		board_title = #{boardTitle}
   		, board_content = #{boardContent}
 WHERE board_no = #{boardNo}

UPDATE board
SET viewCnt = viewCnt + 1
WHERE board_no = #{boardNo}
