mapper.insert:

		SELECT NVL(MAX(board_no),0) + 1
		FROM board

INSERT INTO board
	(
		board_no
		, board_title
		, board_content
		, employee_no
		, write_date
		)	
	VALUES
	(
		#{boardNo}
		, #{boardTitle}
		, #{boardContent}
		, #{employeeNo}
		, sysdate
	)

mapper.update:
UPDATE board
   SET
   		board_title = #{boardTitle}
   		, board_content = #{boardContent}
   		, employeeNo = #{employeeNo}
   		, write_date = sysdate
 WHERE board_no = #{boardNo}
