mapper.select:
SELECT department_name,department_no,manager 
FROM departments 
WHERE company_code = #{companyCode}

SELECT rank_name,rank_id,rank_ranking 
FROM rank 
WHERE company_code = #{companyCode}

mapper.insert:
INSERT INTO employees (
	employee_no
	,employee_name
	,employee_resp
	,company_code
	,employee_id
	,employee_pw
	,department_name
	,rank_name
	)
VALUES(
	emp_seq.NEXTVAL
	,#{employeeName}
	,#{employeeResp}
	,#{companyCode}
	,#{employeeId}
	,#{employeePw}
	,#{departmentName}
	,#{rankName}
	)
