mapper.insert:
INSERT INTO approval(appr_no
					, doc_no
					, employee_no
					, sequence
					, company_code
					, appr_status
					, appr_line
) VALUES (appr_seq.NEXTVAL
		 , #{docNo}
		 , #{employeeNo}
		 , #{sequence}
		 , #{companyCode}
		 , '결재대기'
		 , #{apprLine}
)

mapper.select:
SELECT e.employee_no, COALESCE(r.rank_ranking, 0) as rank_ranking FROM employees e JOIN rank r ON e.rank_name = r.rank_name AND e.company_code = r.company_code WHERE e.employee_no IN
#{id}
AND e.company_code = #{companyCode}

		SELECT COUNT(*)
   		FROM approval
    	WHERE employee_no = #{employeeNo}
      	AND doc_no = #{docNo}
      	AND company_code = #{companyCode}
      	AND sequence = appr_line
      	AND appr_status = '결재대기'

mapper.update:
UPDATE approval
SET appr_status = '결재완료'
   , sign_date = sysdate
WHERE employee_no = #{employeeNo}
AND doc_no = #{docNo}
AND company_code = #{companyCode}

UPDATE approval
SET appr_line = appr_line + 1
WHERE appr_status = '결재대기'
AND doc_no = #{docNo}
AND company_code = #{companyCode}

mapper.select:
SELECT appr_no
FROM approval
WHERE employee_no = #{employeeNo}
AND doc_no = #{docNo}
AND company_code = #{companyCode}
AND appr_status = '결재완료'

		SELECT COUNT(*)
   		FROM approval
    	WHERE employee_no = #{employeeNo}
      	AND doc_no = #{docNo}
      	AND company_code = #{companyCode}
      	AND sequence = appr_line
      	AND appr_status = '결재완료'

mapper.update:
UPDATE approval
SET appr_status = '반려'
	, appr_reason = #{apprReason}
WHERE doc_no = #{docNo}
AND company_code = #{companyCode}
AND employee_no = #{employeeNo}

		UPDATE approval
		SET appr_status = '미결'
		WHERE doc_no = #{docNo} 
  		AND company_code = #{companyCode}
  		AND sequence = (
      					SELECT sequence 
      					FROM approval 
      					WHERE doc_no = #{docNo} 
        				AND company_code = #{companyCode} 
        				AND sequence > (
            							SELECT sequence 
            							FROM approval 
            							WHERE doc_no = #{docNo} 
              							AND company_code = #{companyCode} 
              							AND employee_no = #{employeeNo}
        								)
  						)
  		AND appr_status = '결재대기'
