mapper.insert:

        	SELECT doc_seq.NEXTVAL FROM DUAL

		INSERT INTO document(doc_no
							, employee_no
							, doc_title
							, doc_name
							, doc_content
							, create_date
							, doc_status
							, company_code
		) VALUES (#{docNo}
				 , #{employeeNo}
				 , #{docTitle}
				 , #{docName}
				 , #{docContent,jdbcType=CLOB}
				 , sysdate
				 , '상신완료'
				 , #{companyCode}
		)

mapper.update:
UPDATE document
SET appr_status = '결재진행중'
WHERE doc_no = #{docNo}
AND company_code = #{companyCode}

UPDATE document
SET appr_status = '반려'
	, doc_status = '반려'
	, finish_date = sysdate
WHERE doc_no = #{docNo}
AND company_code = #{companyCode}

	UPDATE document
	SET appr_status = '결재완료'
		, doc_status = '결재완료'
		, finish_date = sysdate
	WHERE doc_no = #{docNo}
  	AND company_code = #{companyCode}
  	AND NOT EXISTS (
      				SELECT 1
      				FROM approval
      				WHERE doc_no = #{docNo}
        			AND company_code = #{companyCode}
        			AND appr_status != '결재완료'
  	)
