mapper.select:
SELECT COUNT(*) 
FROM company 
WHERE company_id = #{companyId}

mapper.insert:

    	SELECT 'YD' 
           || TO_CHAR(SYSDATE, 'YYMM')
           || LPAD(NVL(MAX(TO_NUMBER(SUBSTR(company_code, -3))), 0) + 1, 3, '0')
    	FROM company
    	WHERE SUBSTR(company_code, 3, 4) = TO_CHAR(SYSDATE, 'YYMM')

	INSERT INTO company(company_code
    	        , company_name
        	    , company_tel
            	, company_id
            	, company_pw
            	, company_business_number
            	, company_type
            	, ceo_name
            	, ceo_email
            	, company_address
            	, ceo_phone
            	, company_post
	) VALUES(#{companyCode}
         	, #{companyName}
         	, #{companyTel}
         	, #{companyId}
         	, #{companyPw}
         	, #{companyBusinessNumber}
         	, #{companyType}
         	, #{ceoName}
         	, #{ceoEmail}
         	, #{companyAddress}
         	, #{ceoPhone}
         	, #{companyPost}
	)

mapper.select:
SELECT company_name
FROM company 
WHERE company_code = #{companyCode}

SELECT company_code
		,company_id
FROM company
WHERE ceo_name = #{ceoName}
And company_business_number = #{companyBusinessNumber}

mapper.update:
	UPDATE company
    SET
        company_pw = #{companyPw}
    WHERE company_code = #{companyCode}
    AND company_id = #{companyId}

mapper.select:
SELECT ceo_email
FROM company
WHERE company_code = #{companyCode}
AND company_id = #{companyId}
