mapper.insert:

    	SELECT 'YD' 
        	|| TO_CHAR(TO_DATE(sysdate,'yyyy-MM-dd'), 'yyMM')
        	|| LPAD((NVL(MAX(TO_NUMBER(SUBSTR(company_code, -1))),0) + 1), 3, '0')
    	FROM company

	INSERT INTO company(company_code
    	        , company_name
        	    , company_tel
            	, company_id
            	, company_pw
            	, company_business_number
            	, company_type
            	, ceo_name
            	, ceo_email
            	, company_address
            	, ceo_phone
            	, company_post
	) VALUES(#{companyCode}
         	, #{companyName}
         	, #{companyTel}
         	, #{companyId}
         	, #{companyPw}
         	, #{companyBusinessNumber}
         	, #{companyType}
         	, #{ceoName}
         	, #{ceoEmail}
         	, #{companyAddress}
         	, #{ceoPhone}
         	, #{companyPost}
	)
