mapper.insert:
		INSERT INTO usemodule(
                         sub_module_no
                        ,module_no
                        ,module_price
                        ,module_name
                        ,company_code
                        ,contract_no
                       )
		VALUES(
		        (SELECT MAX(sub_module_no)+1
		         FROM usemodule)
		       ,(SELECT module_no
		         FROM module
		         WHERE module_name = #{moduleName}
		         )
		       ,(SELECT module_price
		         FROM module
		         WHERE module_name = #{moduleName}
		         )
		       ,#{moduleName}
		       ,#{companyCode}
		       ,#{contractNo}
		       )


			SELECT  NVL(MAX(pay_no), 0) + 1
	         FROM pay

		INSERT INTO pay (
                  pay_no
                 ,users_cnt
                 ,company_code
                 ,contract_no
                 ,order_id
                 ,pay_price
                )
		VAlUES(
		         #{payNo}
		        ,#{usersCnt}
		        ,#{companyCode}
		        ,#{contractNo}
		        ,#{orderId}
		        ,#{monthPayPrice}		        
		          )

	INSERT INTO pay_detail (
                         pay_detail_no
                        ,pay_no
                        ,module_no
                        ,module_price
                        ,module_name
	                        )
	VALUES(
	        (SELECT NVL(MAX(pay_detail_no),0) +1
	         FROM pay_detail)
	        ,#{payNo}
	        ,(SELECT module_no
		         FROM module
		         WHERE module_name = #{moduleName}
		         )
	        ,(SELECT module_price
		         FROM module
		         WHERE module_name = #{moduleName}
		         )
	        ,#{moduleName}  
	        )

mapper.update:
UPDATE company
SET site_resp = 'USER'
